$.ajaxSetup({
  headers: { "X-CSRF-TOKEN": $('meta[name="csrf-token"]').attr("content") },
}),
  $(document).ready(function () {
    $("#loading_box").data("error");
    $("body").on("click", ".navigation-nav .icon-down", function () {
      $(this).closest("li").addClass("active"),
        $(this).closest("li").find("> .submenu").slideToggle();
    }),
      $("body").on("click", ".header-middle .left-menu", function () {
        $(".navigation").addClass("active");
      }),
      $("body").on("click", ".navigation-top__close", function () {
        $(".navigation").removeClass("active");
      }),
      $("body").on("click", ".open-searc-box", function () {
        $(".search-box").toggleClass("active");
      }),
      $("body").on("click", ".search-box__wrap .close", function () {
        $(".search-box").toggleClass("active");
      });
      $("body").on("click", "p.search-button", function () {
        $(".search-box").toggleClass("active");
      });
  }),
  $(document).mouseup(function (a) {
    var o = $(".navigation-wrap");
    o.is(a.target) ||
      0 !== o.has(a.target).length ||
      o.parent().removeClass("active");
  });
